INSERT INTO zj_index_place VALUES
(NULL, '中国 · 大连', '大连，别称滨城、北方明珠、浪漫之都、足球场，隶属于辽宁省，地处欧亚大陆东岸，中国东北辽宁半岛最南端', 'img/index/floor1-1.png', 'details.html?lid=1',1,NULL,NULL,NULL),
(NULL, '中国 · 北京', '北京是中华人民共和国的首都。有着世界闻名的故宫、长城......', 'img/index/floor1-2.png', 'details.html?lid=2',2,NULL,NULL,NULL),
(NULL, '澳大利亚 · 怀特黑文海滩', '怀特黑文海滩位於岛的三位一体，海滩约6公里，它由细白的沙滩，这片令人叹为观止的海滩位于圣灵岛，面积4.3平方英里，它有“昆士兰州最清洁的海滩”之称', 'img/index/floor1-4.png', 'details.html?lid=3',3,NULL,NULL,NULL),
(NULL, '埃及 · 金字塔', '埃及金字塔是古埃及法老（即国王）和王后的陵墓。古埃及金字塔是一种方锥形建筑物。最大、最有名的是祖孙三代金字塔——胡夫金字塔、哈夫拉金字塔和门卡乌拉金字塔（孟卡拉金字塔)，其中以胡夫金字塔最为出名', 'img/index/floor1-5.png', 'details.html?lid=4',4,NULL,NULL,NULL),
(NULL, '意大利 · 乔托钟楼', '佛罗伦萨主教堂的乔托钟楼，哥特式建筑在意大利并不很多，这是因为意大利在这个时期未取得统一。佛罗伦萨是意大利的中心城市，在欧洲经济中占有重要地位，也是最早战胜封建领主并建立城市自治的一个共和国', 'img/index/floor1-3.png', 'details.html?lid=5',5,NULL,NULL,NULL),
(NULL, '德国 · 国王湖', '国王湖位于德国和奥地利边境的小城贝希特斯加登旁，靠近阿尔卑斯山脉。是德国阿尔卑斯山区最优美的地区。是德国最深的湖泊', 'img/index/floor2-1.png', 'details.html?lid=6',NULL,1,NULL,NULL),
(NULL, '澳大利亚 · 禧利湖', '西澳大利亚州的赫特泻湖(Hutt Lagoon)呈现美丽的粉红色，就像一个羞涩的新娘', 'img/index/floor2-2.png', 'details.html?lid=7',NULL,2,NULL,NULL),
(NULL, '马尔代夫 · 满月岛', '满月海滩度假村（Full Moon Beach Resort）在马尔代夫也十分有名，从机场乘坐摩托艇20分钟即可到达，距离大约6公里。当地人属于南亚黑色人种，肤色偏黑', 'img/index/floor2-3.png', 'details.html?lid=8',NULL,3,NULL,NULL),
(NULL, '意大利 · 五渔村', '五渔村(意大利语：Cinque Terre，又译：五乡地、五村镇)位于意大利利古里亚大区(Liguria)拉斯佩齐亚省海沿岸地区', 'img/index/floor2-4.png', 'details.html?lid=9',NULL,4,NULL,NULL),
(NULL, '冰岛 · 斯卡夫塔费德冰洞', '斯瓦蒂佛斯(Svartifoss，冰岛语“黑色瀑布”的意思)位于冰岛斯卡夫塔费德国家公园', 'img/index/floor2-5.png', 'details.html?lid=10',NULL,5,NULL,NULL),
(NULL, '神农架', '神农架是中国首个获得联合国教科文组织人与生物圈自然保护区、世界地质公园、世界遗产三大保护制度共同录入的“三冠王”名录遗产地。神农架因华夏始祖炎帝神农氏在此架木为梯，采尝百草，救民疾夭，教民稼穑而得名', 'img/index/floor3-1.png', 'details.html?lid=11',NULL,NULL,1,NULL),
(NULL, '新疆', '新疆维吾尔自治区，简称新，位于中国西北边陲，首府乌鲁木齐，是中国五个少数民族自治区之一，也是中国陆地面积最大的省级行政区，面积166万平方公里，占中国国土总面积六分之一', 'img/index/floor3-2.png', 'details.html?lid=12',NULL,NULL,2,NULL),
(NULL, '海南', '海南全省陆地（主要包括海南岛和西沙、中沙、南沙群岛）。海南岛是仅次于台湾岛的中国第二大岛，海南省是中国国土面积（含海域）第一大省', 'img/index/floor3-3.png', 'details.html?lid=13',NULL,NULL,3,NULL),
(NULL, '福建泉州', '泉州是全国首个东亚文化之都，联合国教科文组织唯一认定的海上丝绸之路起点。是全国首个东亚文化之都，联合国教科文组织唯一认定的海上丝绸之路起点，是列入国家“一带一路”战略的21世纪海上丝绸之路先行区', 'img/index/floor3-4.png', 'details.html?lid=14',NULL,NULL,4,NULL),
(NULL, '宁夏银川', '银川是历史悠久的塞上古城，早在3万年前就有人类在水洞沟遗址繁衍生息，史上西夏王朝的首都，是国家历史文化名城，民间传说中又称“凤凰城”', 'img/index/floor3-5.png', 'details.html?lid=15',NULL,NULL,5,NULL),
(NULL, '台湾 · 月亮桥', '台湾台北的大湖公园，石拱桥弯弯的圆拱像月亮一般圆润美丽，所以可能在不知其本名为锦带桥的情况下，国外媒体称之为月亮桥', 'img/index/floor4-1.png', 'details.html?lid=16',NULL,NULL,NULL,1),
(NULL, '台湾', '台湾（Taiwan）位于中国大陆东南沿海的大陆架上，东临太平洋，南界巴士海峡与菲律宾群岛相对，西隔台湾海峡与福建省相望', 'img/index/floor4-2.png', 'details.html?lid=17',NULL,NULL,NULL,2),
(NULL, '澳门', '澳门（葡语Macau、英语Macao），全称为中华人民共和国澳门特别行政区。北邻广东省珠海市，西与珠海市的湾仔和横琴对望，东与香港隔海相望，相距60公里，南临中国南海', 'img/index/floor4-3.png', 'details.html?lid=18',NULL,NULL,NULL,3),
(NULL, '香港', '香港是全球第三大金融中心，重要的国际金融、贸易和航运中心，与纽约、伦敦并称为“纽伦港”，是全球最自由经济体和最具竞争力城市之一，在世界享有极高声誉，被GaWC评为世界一线城市。地处中国华南地区，珠江口以东，南海沿岸，北接广东省深圳市，西接珠江，与澳门特别行政区、珠海市以及中山市隔着珠江口相望', 'img/index/floor4-4.png', 'details.html?lid=19',NULL,NULL,NULL,4),
(NULL, '澳门', '澳门是一个国际自由港，是世界人口密度最   高的地区之一，也是世界四大赌城之一，实行资本主义制度。2017年10月31日，联合国教科文组织将澳门列入全球创意城市网络', 'img/index/floor4-5.png', 'details.html?lid=20',NULL,NULL,NULL,5);
