SET NAMES UTF8;
DROP DATABASE IF EXISTS zj;
CREATE DATABASE zj CHARSET=UTF8;
USE zj;

/***用户信息***/
CREATE TABLE zj_user(
	uid INT PRIMARY KEY AUTO_INCREMENT,         				    #用户ID
	uname VARCHAR(32) UNIQUE NOT NULL,          				    #用户登录名称
	upwd VARCHAR(32) NOT NULL,                  				    #用户登陆密码
	email VARCHAR(64) NOT NULL,                 				    #用户邮箱
	phone VARCHAR(16) NOT NULL,                 				    #用户电话号码
	avater VARCHAR(128) DEFAULT 'img/avatar/default.png',       	#用户头像图片路径
	user_name VARCHAR(32),                      				    #用户名 如王小明
	gender INT                                  				    #性别  0-女  1-男
);

/***首页景点***/
CREATE TABLE zj_index_place(
	pid INT PRIMARY KEY AUTO_INCREMENT,         		#景点ID
	title VARCHAR(64),                          		#景点标题
	details VARCHAR(480),                       		#景点详细信息
	pic VARCHAR(128),                           		#景点图片路径
	href VARCHAR(64),                           		#景点图片链接
	hot INT,											#热门搜素
	haiwai INT,								    		#海外美景
	dalu INT,											#大陆美景
	gat INT 											#港澳台美景
);

/***景点列表***/
CREATE TABLE zj_place(
	pid INT PRIMARY KEY AUTO_INCREMENT,					#景点ID
	title VARCHAR(64),									#景点标题
	placeloc VARCHAR(64),								#景点所在地
	jdtype VARCHAR(64),									#景点类型
	level VARCHAR(64) DEFAULT '无',						#景区级别
	season VARCHAR(64),									#合适季节
	imghref VARCHAR(64),								#景区图片路径
	href VARCHAR(128)									#景区图片链接
);

/***景点***/
CREATE TABLE zj_details(
	lid INT PRIMARY KEY AUTO_INCREMENT,					        	#景点ID
	title VARCHAR(64),                                              #景点标题
	detailstext1 VARCHAR(1024),									    #详情信息1
	detailstext2 VARCHAR(1024),									    #详情信息2
	detailstext3 VARCHAR(1024)									    #详情信息3
);

/***景点图片表***/
CREATE TABLE zj_pic(
    pid INT PRIMARY KEY AUTO_INCREMENT,              #景点图片id
    lid INT,                                         #景点编号
    imghref VARCHAR(64)                              #景点图片路径
);

/***景点视频***/
CREATE TABLE zj_video(
    vid INT PRIMARY KEY AUTO_INCREMENT,              #景点视频id
    lid INT,                                         #景点编号
    vhref VARCHAR(64) DEFAULT 'img/src/err.jpg'   	 #景点视频路径
);

/***首页轮播广告商品***/
CREATE TABLE zj_index_carousel(
	cid INT PRIMARY KEY AUTO_INCREMENT,       			#图片id
	img VARCHAR(128),	                        		#图片路径
	title VARCHAR(64)	                        		#图片描述
);

/***用户留言区***/
CREATE TABLE zj_message(
	mid INT PRIMARY KEY AUTO_INCREMENT,					#留言id
	uid INT,											#用户id
	FOREIGN KEY(uid) REFERENCES zj_user(uid),
	timer VARCHAR(64),									#发表时间
	content VARCHAR(1024)								#留言内容
);

